function.body:
v_Sql VARCHAR2(32767) ;
  v_result VARCHAR2(32767) ;
  v_tablename  VARCHAR2(32767) ;
  BEGIN

	select tablename into v_tablename from ad_table where ad_table_id = p_ad_table_id;
	v_Sql:= 'select  description from ' ||v_tablename || ' where ' || v_tablename || '_id = ' || '''' || p_record_id || '''' ;
	EXECUTE IMMEDIATE v_Sql INTO v_result;	
		RETURN(coalesce(v_result,'')) ;
END SSFI_GET_DESCTRANS
