sfcbr_inc_statmnt_script.rEPSql:
select codigo, cuenta, valor,typeheader from ( select null as codigo,'RESULTADOS INTEGRALES' as cuenta, round(sum(valor),2) as valor , '1' as typeheader from (
select account_id as codigo ,name as cuenta ,round(sum(debe),2) as valor ,'2' as typeheader from ( select a.id, ev.value as account_id, name as name, a.initialamt as SALDO_INICIAL, a.amtacctdr as debe , a.amtacctcr as haber, A.INITIALAMT+A.AMTACCTDR-A.AMTACCTCR AS SALDO_FINAL from( SELECT sum(case when (DATEACCT < TO_DATE(':p_df') or (DATEACCT = TO_DATE(':p_df') and F.FACTACCTTYPE = 'O')) then F.AMTACCTDR - F.AMTACCTCR else 0 end) as initialamt, sum(case when (DATEACCT >= TO_DATE(':p_df') AND F.FACTACCTTYPE not in('O', 'R', 'C')) or (DATEACCT = TO_DATE(':p_df') and F.FACTACCTTYPE = NULL) then F.AMTACCTDR else 0 end) as AMTACCTDR, sum(case when (DATEACCT >= TO_DATE(':p_df') AND F.FACTACCTTYPE not in('O', 'R', 'C')) or (DATEACCT = TO_DATE(':p_df') and F.FACTACCTTYPE = NULL) then F.AMTACCTCR else 0 end) as AMTACCTCR, F.ACCOUNT_ID AS ID, to_char('') AS groupbyid FROM FACT_ACCT F WHERE date(DATEACCT) <= TO_DATE(':p_dt') AND F.ISACTIVE='Y' and (f.ad_org_id = ':p_adorg' OR COALESCE(':p_adorg','ND')='ND') group by F.ACCOUNT_ID ) a, c_elementvalue ev where a.id = ev.c_elementvalue_id and ev.elementlevel = 'S' and EV.VALUE in (:p_account) and (a.initialamt <>0 or a.amtacctcr <>0 or a.amtacctdr<>0) order by ev.value, ev.name, id ) rep group by name, account_id
) rep0
) rep
