trigger.body:
  v_Countline NUMBER;
        
BEGIN
    
    IF AD_isTriggerEnabled()='N' THEN RETURN;
    END IF;
    
    IF (UPDATING OR INSERTING) THEN  

		IF(:new.paidinvoice = 'N')THEN
			:New.C_Glitem_ID:=null;
		ELSE
			IF(:New.C_Glitem_ID is null and :New.generate_advance_payment = 'N' )THEN
			RAISE_APPLICATION_ERROR(-20000, 'Necesita escoger un concepto contable cuando estÃ¡ activo el check de factura pagada.') ;
			END IF;
		END IF;

	END IF;
    
END SSWS_CHCK_PAIDINVOICE_TRG
